import PropTypes from 'prop-types';
import React from "react";
import cx from "classnames";
import solrQuery2 from "../../api/solr-query";

class CurrentQueryRaw extends React.Component {

        render() {
                const { bootstrapCss, query} = this.props;
		var root = document.getElementById('app');
		var cible = root.getAttribute('cible');
		var req = root.getAttribute('req');
		if (typeof req === "undefined"){req = "undefined req";};
		if (req.length === 0){req = "empty req";};
                return (
			<div>
			<div>cible : {cible}</div>
			<div>requete : <pre>{req}</pre></div>
			<div>{query.url}</div>
			<div><pre>{solrQuery2(query)}</pre></div>
			</div>
		);}
}

CurrentQueryRaw.propTypes = {
	bootstrapCss: PropTypes.bool,
	onChange: PropTypes.func,
	query: PropTypes.object
};

export default CurrentQueryRaw;
